import React, { Fragment, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import styles from "./Home.module.css";
import { getUsername } from "../../helper";
import Mail from "./Mail";

const Sent = () => {
  let mails = [];
  const [inboxMail, setInboxMail] = useState([]);
  const user = localStorage.getItem("email");
  const username = getUsername(user);

  // useEffect(() => {
  //   fetch(
  //     `https://project-1d90b-default-rtdb.firebaseio.com/${username}/sent.json`
  //   )
  //     .then((res) => {
  //       return res.json();
  //     })
  //     .then((data) => {
  //       for (let [key, value] of Object.entries(data)) {
  //         mails.push({ key, ...value });

  //       }
  //       setInboxMail(mails);
  //     })
  // }, []);

useEffect(() => {
  (async () => {
    const response = await fetch(
      `https://project-1d90b-default-rtdb.firebaseio.com/${username}/sent.json`
    );
    const data = await response.json();
    const mails = [];
    for (let [key, value] of Object.entries(data)) {
      mails.push({ key, ...value });
    }
    setInboxMail(mails);
  })();
}, []);

  const deleteHandler = (key) => {
    fetch(`https://project-1d90b-default-rtdb.firebaseio.com/${username}/sent/${key}.json`, {
      method: "DELETE",
    }).then((res) => {
      const index = inboxMail.findIndex((item) => item.key === key);
      inboxMail.splice(index, 1)
      setInboxMail(inboxMail);
      window.location.reload();
    })
  }


  return (
    <Fragment>
      <div className={styles.container}>
        <div className={styles.sidebar}>
          <NavLink
            to="/home"
            className={styles.none}
            activeClassName={styles.active}
          >
            {" "}
            <div>Compose</div>
          </NavLink>
          <NavLink
            to="/inbox"
            className={styles.none}
            activeClassName={styles.active}
          >
            <div>
              Inbox
            </div>
          </NavLink>
          <NavLink
            to="/sent"
            className={styles.none}
            activeClassName={styles.active}
          >
            {" "}
            <div>Sent</div>
          </NavLink>
        </div>

        <div className={styles.mailsContainer}>
          {inboxMail.map((mail) => {
            return <Mail key={mail.key} mail={mail} isSentBox={true} deleteItem={deleteHandler} />;
          })}
        </div>
      </div>
    </Fragment>
  );
};
export default Sent;
